private void passMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_passMouseClicked
if (    "".equals(mat.getText())){JOptionPane.showMessageDialog(null,"champ matricule ne doit pas etre vide");
jButton1.setEnabled(false);
}else{jButton1.setEnabled(true);
try{
    stmt=cnx.obtenircon().createStatement();
    ResultSet rst=stmt.executeQuery("select matricule from users");
    while(rst.next()){
        if(rst.getInt("matricule")==Integer.parseInt(mat.getText())){
            JOptionPane.showMessageDialog(null,"matricule existe");
            jButton1.setEnabled(false);
        }
    }
}catch(SQLException e){
    JOptionPane.showMessageDialog(null,e);
}}
}//GEN-LAST:event_passMouseClicked

private void passFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_passFocusLost
if(     "".equals(pass.getText())){
    JOptionPane.showMessageDialog(null,"champ mot de passe ne doit pas etre vide");
    jButton1.setEnabled(false);
}else{
    jButton1.setEnabled(true);
}
}//GEN-LAST:event_passFocusLost

