private void createaccount(){
try{
        stmt=cnx.obtenircon().createStatement();
    stmt.executeUpdate("insert into users (matricule, password, fonction) values ('"+Integer.parseInt(mat.getText())+"','"+pass.getText()+"','"+fct.getSelectedItem().toString()+"')");
    JOptionPane.showMessageDialog(null,"Votre compte a été creé");
        }catch(SQLException e){
        }
        
}
private void checkadmin(){
String passw="";
    try{
            stmt=cnx.obtenircon().createStatement();
            ResultSet rst = stmt.executeQuery("select password from users where fonction = 'Administrateur'");
            while(rst.next()){
                passw = rst.getString("password");
            }
            if(     !"".equals(passw)){JOptionPane.showMessageDialog(null,"un compte administrateur existe déja");
            jButton1.setEnabled(false);
            }else{
            jButton1.setEnabled(true);
            createaccount();
            } 
        }catch(SQLException e){}
}
private void checkadming(){
    String passw="";
    try{
    stmt=cnx.obtenircon().createStatement();
            ResultSet rst = stmt.executeQuery("select password from users where fonction = 'Administrateur garage'");
            while(rst.next()){
                passw = rst.getString("password");
            }
            if(     !"".equals(passw)){JOptionPane.showMessageDialog(null,"un compte administrateur existe déja");
            jButton1.setEnabled(false);
            }else{
            jButton1.setEnabled(true);
            createaccount();
            }  
    }catch(SQLException e){
    }
     
}
