private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
int[] mat = new int[50];
int i=0;
    SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
java.util.Date date_loc=date_location.getDate();
java.util.Date date_ret=date_retour.getDate();
String datel = sdf.format(date_loc);
String dater = sdf.format(date_ret);
try{
    //if(mat!=Integer.parseInt(matricule.getText())){
    stmt=cnx.obtenircon().createStatement();
    stmt.executeUpdate("insert into vehicules_louees values ('"+num_serie.getText()+"','"+Integer.parseInt(num.getText())+"','"+Integer.parseInt(serie.getText())+"','"+marque.getText()+"','"+puissance.getText()+"','"+Integer.parseInt(indexkm.getText())+"','"+datel +"','"+dater+"','"+Integer.parseInt(matricule.getText())+"','"+nom.getText()+"','"+prenom.getText()+"','"+service.getText()+"')");
    JOptionPane.showMessageDialog(null, "véhicule affecté");
    annuler();
    //}else{JOptionPane.showMessageDialog(null, "une véhicule est déja affecté a cet agent");}  
}catch(SQLException exp){
    JOptionPane.showMessageDialog(null, "erreur d'affectation");
}

}//GEN-LAST:event_jButton1ActionPerformed

private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
int res = JOptionPane.showConfirmDialog(null, "Voulez vous vraiment supprimer la véhicule de la liste?");
if(res==0){
    try{
        stmt=cnx.obtenircon().createStatement();
        stmt.execute("delete from vehicules_louees where num_serie="+num_serie.getText());
        JOptionPane.showMessageDialog(null,"bien supprimé");
        annuler();
}catch(SQLException e){
    JOptionPane.showMessageDialog(null,e);
}
}else{JOptionPane.showMessageDialog(null,"annulé");}
}//GEN-LAST:event_jButton4ActionPerformed

private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
    try{
        stmt=cnx.obtenircon().createStatement();
        ResultSet rst =stmt.executeQuery("select numero,  serie, marque, puissance, indexkm, date_reservation, date_retour, matricule_agent, nom, prenom, service from vehicules_louees where num_serie='"+num_serie.getText()+"'");
        while(rst.next()){
            num.setText(rst.getString("marque"));
            serie.setText(rst.getString("serie"));
            marque.setText(rst.getString("marque"));
            puissance.setText(rst.getString("puissance"));
            indexkm.setText(rst.getString("indexkm"));
            date_location.setDate(rst.getDate("date_reservation"));
            date_retour.setDate(rst.getDate("date_retour"));
            matricule.setText(rst.getString("matricule_agent"));
            nom.setText(rst.getString("nom"));
            prenom.setText(rst.getString("prenom"));
            service.setText(rst.getString("service"));
        }
}catch(SQLException e){
  JOptionPane.showMessageDialog(null, e);  
}

}//GEN-LAST:event_jButton5ActionPerformed

