private void annuler (){
num_serie.setText("");num.setText("");serie.setText("");marque.setText("");puissance.setText("");indexkm.setText("");matricule.setText("");nom.setText("");prenom.setText("");service.setText("");

date_location.setToolTipText(null);
date_retour.setToolTipText(null);


}
private void infoagent(){
try{
    stmt=cnx.obtenircon().createStatement();
    ResultSet rst = stmt.executeQuery("select nom, prenom, service from personnels where mat='"+Integer.parseInt(matricule.getText())+"'");
    while(rst.next()){
        nom.setText(rst.getString("nom"));
        prenom.setText(rst.getString("prenom"));
        service.setText(rst.getString("service"));
    }
}catch(SQLException e){
    
}
}
